{
  arr.push(d.getDate()+"/"+d.getMonth());
  v.push(i);
  //console.log(d)
  //console.log(d.getTime() == new Date(2020,0,20).getTime())

  if (d.getTime() == new Date(2020,0,20).getTime())
  {//china
    daily_saving+=57.31496;
    //console.log("in side china")
